.list-link:focus {
  position: absolute;
  top: 0;
  left: 0;
  background: var(--color-bg-body);
  border: 1px solid #eeeeee;
  box-sizing: border-box;
  box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.12), 0px 4px 4px rgba(0, 0, 0, 0.06),
    1px 4px 6px rgba(0, 0, 0, 0.16);
  transition-property: box-shadow;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.list-link__overflow-img {
  position: relative;
  overflow: hidden;
}

.list-link__overflow {
  position: absolute;
  width: 100%;
  height: 100%;
  background: rgba(33, 150, 243, 0.9);
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: left;
  transform: translateY(100%);
  transition-property: transform;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.list-link:hover .list-link__overflow,
.list-link:focus .list-link__overflow {
  transform: translatey(-100%);
  transition-property: transform;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.list-link__overflow-text {
  font-size: 18px;
  line-height: 1.56;
  max-width: 322px;
  max-height: 168px;
  letter-spacing: 0.03em;
  color: var(--color-bg-body);
}

.list-link__text {
  border-bottom: 1px solid #eeeeee;
  border-left: 1px solid #eeeeee;
  border-right: 1px solid #eeeeee;
  text-align: left;
  padding: 20px 24px;
}

.list-link__subtitle {
  font-weight: 700;
  font-size: 17px;
  line-height: 2;
  letter-spacing: 0.06em;
  color: var(--color-text1);
}

.list-link__content {
  font-size: 16px;
  line-height: 1.87;
  letter-spacing: 0.03em;
  color: var(--color-text2);
}

.backdrop {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.4);
}

.modal__close-btn {
  width: 30px;
  height: 30px;
  position: absolute;
  top: 8px;
  right: 8px;
  border-radius: 50%;
  border: 1px solid #afb1b8;
  background-color: var(--color-bg-body);
}

.modal__close-btn:hover,
.modal__close-btn:focus {
  border: 1px solid var(--color-link-focus);
  cursor: pointer;
  fill: var(--color-link-focus);
}

.modal {
  width: 528px;
  height: 581px;
  background-color: var(--color-bg-body);
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  padding: 40px;
}

.modal__close-icon {
  display: inline-block;
  width: 18px;
  height: 18px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.is-hidden {
  visibility: hidden;
  opacity: 0;
  pointer-events: none;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal-form {
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.modal-form__title {
  font-weight: bold;
  font-size: 20px;
  line-height: 1.15;
  text-align: center;
  letter-spacing: 0.03em;
  color: var(--color-text1);
}

.modal-form__subbox {
  position: relative;
  display: flex;
  flex-direction: column;
  margin-top: 12px;
}

.modal-form__subbox:not(:first-child) {
  position: relative;
  margin-top: 10px;
}

.modal-form__label {
  margin-bottom: 4px;
  color: var(--color-text2);
  font-weight: 400;
  font-size: 12px;
  line-height: 1.17;
  letter-spacing: 0.01em;
}

.modal-form__icon {
  position: absolute;
  top: 50%;
  left: 12px;
  display: flex;
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal-form__input:focus + .modal-form__icon {
  fill: var(--color-link-focus);
}

.modal-form__input {
  height: 40px;
  width: 100%;
  padding-left: 30px;
  border-radius: 4px;
  border: 1px solid rgba(33, 33, 33, 0.2);
  transition-property: border-color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal-form__input:focus,
.modal-form__input:hover {
  outline: none;
  border-color: var(--color-link-focus);
  transition-property: border-color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal-form__comment {
  padding: 12px 16px;
  height: 120px;
  resize: none;
  border-radius: 4px;
  border: 1px solid rgba(33, 33, 33, 0.2);
  transition-property: outline-color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal-form__comment:focus,
.modal-form__comment:hover {
  outline: none;
  border-color: var(--color-link-focus);
  transition-property: border-color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.modal-form__checkbox-label {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 20px;
  cursor: pointer;
}

.check-wrapper {
  position: relative;
  margin-right: 8px;
}

.modal-form__checkbox {
  display: block;
  width: 16px;
  height: 15px;
  appearance: none;
  border: 2px solid var(--color-text1);
  border-radius: 1px;
}

.modal-form__checkbox:focus {
  border: 2px solid var(--color-link-focus);
  outline: 2px solid transparent;
}

.modal-form__checkbox:checked {
  fill: currentColor;
  background-color: var(--color-link-focus);
  border: none;
}

.modal-form__policy-text {
  color: var(--color-text2);
  font-weight: 400;
  font-size: 14px;
  line-height: 1.71;
  letter-spacing: 0.03em;
  color: #757575;
}

.modal-form__policy-link {
  color: var(--color-link-focus);
  text-decoration: underline;
}

.modal-form__check-icon {
  fill: var(--color-bg-body);
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 11px;
  height: 8px;
}

.modal-form__checkbox:checked + .modal-form__check-icon {
  fill: var(--color-bg-body);
}

.modal-form__button {
  display: flex;
  justify-content: center;
  margin-top: 30px;
}

.modal-form__submit {
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
}

sourcesContent:
:root {
  --color-text1: #212121;
  --color-text2: #757575;
  --color-link-focus: #2196f3;
  --color-bg-body: #ffffff;
  --color-bg-btn: #f5f4fa;
  --color-hero: #2f303a;
  --color-hero-dg: #c4c4c4;
}

html {
  box-sizing: border-box;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

.body {
  color: #212121;
  background-color: #ffffff;
  font-family: Roboto, sans-serif;
  font-style: normal;
  margin: 0;
  outline: 1px solid brown;
}

ul {
  list-style: none;
  margin: 0;
  padding: 0;
  /* outline: 2px solid blueviolet; */
}

img {
  display: block;
  max-width: 100%;
  height: auto;
}

h1,
h2,
h3,
p {
  margin: 0;
  padding: 0;
}

.visually-hidden {
  position: absolute !important;
  clip: rect(1px 1px 1px 1px);
  clip: rect(1px, 1px, 1px, 1px);
  padding: 0 !important;
  border: 0 !important;
  height: 1px !important;
  width: 1px !important;
  overflow: hidden;
}

.container {
  width: 100%;
  padding-left: 15px;
  padding-right: 15px;
  margin: 0 auto;

  flex-direction: row;
  justify-content: center;
  outline: 1px solid brown;
  /* display: flex; */
}

@media screen and (min-width: 480px) {
  .container {
    max-width: 480px;
    margin: 0 auto;
  }
  .hero {
    max-width: 480px;
    margin: 0 auto;
    flex-wrap: wrap;
  }
  .footer {
    max-width: 480px;
    margin: 0 auto;
    flex-wrap: wrap;
  }
}

@media screen and (min-width: 768px) {
  .container {
    max-width: 768px;
    margin: 0 auto;
  }
  .hero {
    max-width: 768px;
    margin: 0 auto;
    flex-wrap: wrap;
  }
  .footer {
    max-width: 768px;
    margin: 0 auto;
    flex-wrap: wrap;
  }
}

@media screen and (min-width: 1200px) {
  .container {
    max-width: 1200px;
    margin: 0 auto;
  }
  .hero {
    max-width: 1200px;
    margin: 0 auto;
    flex-wrap: nowrap;
  }
  .footer {
    max-width: 1200px;
    margin: 0 auto;
    flex-wrap: nowrap;
  }
}

header {
  min-height: 80px;
  border-bottom: 1px solid #ececec;
  display: flex;
}

header .container {
  display: flex;
  align-items: center;
}

.section {
  padding-bottom: 94px;
}

.link {
  display: inline-block;
  color: var(--color-text1);
  font-weight: 500;
  font-size: 14px;
  line-height: 1.14;
  letter-spacing: 0.02em;
  text-decoration: none;
}

.link:hover,
.link:focus {
  color: #2196f3;
  transition-property: color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.feedback {
  padding: 32px 0;
  display: flex;
  justify-content: center;
  align-items: center;
  color: var(--color-text2);
  transition-property: color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.feedback:hover,
.feedback:focus {
  color: #2196f3;
  transition-property: color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.nvg-list__link {
  transition-property: color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  padding-bottom: 32px;
  padding-top: 32px;
}

.nvg-list__link::after {
  content: "";
  display: block;
  position: absolute;
  width: 100%;
  height: 4px;
  /* background: #2196f3; */
  background: #2196f3;
  border-radius: 2px;
  bottom: 0;
  opacity: 0;
  transition-property: opacity;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.nvg-list__link:hover,
.nvg-list__link:focus {
  transition-property: color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.nvg-list__link:hover::after,
.nvg-list__link:focus::after {
  opacity: 1;
  transition-property: color, opacity;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.nvg-list__link-studio {
  font-weight: 500;
  font-size: 14px;
  line-height: 1.25;
  letter-spacing: 0.02em;
  color: #2196f3;
}

.nvg-list__link-studio::after {
  opacity: 1;
  content: "";
  display: block;
  position: absolute;
  width: 100%;
  height: 4px;
  background: #2196f3;
  border-radius: 2px;
  bottom: 0;
  opacity: 1;
  transition-property: opacity;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.nvg-list__link-portfolio {
  font-weight: 500;
  font-size: 14px;
  line-height: 1.25;
  letter-spacing: 0.02em;
  color: #2196f3;
}

.nvg-list__link-portfolio::after {
  opacity: 1;
  content: "";
  display: block;
  position: absolute;
  width: 100%;
  height: 4px;
  background: #2196f3;
  border-radius: 2px;
  bottom: 0;
  opacity: 1;
  transition-property: opacity;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.logo {
  font-family: Raleway, sans-serif;
  font-weight: 700;
  font-size: 26px;
  line-height: 1.19;
  letter-spacing: 0.03em;
  text-decoration: none;
  color: #2196f3;
}

.header__logo {
  padding-top: 24px;
  padding-bottom: 24px;
  margin-right: 90px;
}

.header__logo-studio {
  display: inline-flex;
  color: #212121;
}

.footer__logo {
  display: inline-block;
  margin-right: auto;
  margin-bottom: 20px;
}

.footer__logo-studio {
  color: var(--color-bg-body);
}

.nvg {
  display: flex;
  justify-content: start;
}

.nvg-list {
  display: flex;
  align-items: center;
}

.nvg-list__item {
  position: relative;
}

.nvg-list__item:not(:last-child) {
  margin-right: 50px;
}

.header-contacts {
  margin-left: auto;
}

.mail-icon {
  width: 16px;
  height: 12px;
  fill: #757575;
  margin-right: 10px;
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.feedback:focus .mail-icon,
.feedback:hover .mail-icon {
  fill: var(--color-link-focus);
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.telefon-icon {
  width: 10px;
  height: 16px;
  fill: var(--color-text2);
  margin-right: 10px;
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.feedback:focus .telefon-icon,
.feedback:hover .telefon-icon {
  fill: var(--color-link-focus);
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.contacts-list {
  display: flex;
  margin-left: auto;
  align-items: center;
}

.contacts-list__item:not(:last-child) {
  margin-right: 50px;
}

.header-contacts__title {
  margin-bottom: 50px;
}

.hero {
  display: flex;
  justify-content: center;
  padding: 0px;
  text-align: center;
  padding: 200px 0;
  /* max-width: 1600px; */
  min-height: 600px;
  margin: 0 auto;
  background-color: var(--color-hero-dg);
  background-image: linear-gradient(
      to top,
      rgba(47, 48, 58, 0.4),
      rgba(47, 48, 58, 0.4)
    ),
    url(../images/hero-img.jpg);
  background-repeat: no-repeat, no-repeat;
  background-size: cover, cover;
}

.hero__title {
  color: var(--color-bg-body);
  font-weight: 900;
  font-size: 44px;
  line-height: 1.36;
  letter-spacing: 0.06em;
  text-transform: uppercase;
  /* padding-bottom: 30px; */
  max-width: 696px;
  margin: 0 auto;
  margin-bottom: 30px;
}

.hero__btn {
  background-color: var(--color-link-focus);
  color: var(--color-bg-body);
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.15);
}

.btn {
  background-color: var(--color-link-focus);
  color: var(--color-bg-body);
  border-radius: 4px;
  font-weight: bold;
  font-size: 16px;
  line-height: 1.87;
  text-align: center;
  letter-spacing: 0.06em;
  cursor: pointer;
  border: none;
  width: 200px;
  height: 50px;
}

.hero__btn {
  background-color: var(--color-link-focus);
  color: var(--color-bg-body);
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.15);
}

.work-section__list {
  display: flex;
  flex-wrap: wrap;
  margin-left: -30px;
  margin-top: -30px;
  justify-content: center;
  flex-wrap: wrap;
}

@media screen and (min-width: 480px) {
  .work-section__list {
    justify-content: center;
    flex-wrap: wrap;
  }
}

.work-section {
  padding-top: 94px;
}

.work-section__item {
  flex-basis: calc((100% - 4 * 30px) / 4);
  margin-left: 30px;
  margin-top: 30px;
  max-width: 270px;
  word-wrap: break-word;
}

.work-icon {
  width: 70px;
  height: 70px;
  fill: var(--color-text1);
}

.work-section__icon {
  display: flex;
  justify-content: center;
  align-items: center;
  max-width: 270px;
  min-height: 120px;
  margin: 0 auto;
  background: var(--color-bg-btn);
  border-radius: 4px;
  margin-bottom: 30px;
}

.work-section__title {
  font-weight: 700;
  font-size: 14px;
  line-height: 1.25;
  letter-spacing: 0.03em;
  text-transform: uppercase;
  color: var(--color-text1);
  margin-bottom: 10px;
}

.work-section__text {
  color: var(--color-text2);
  font-size: 14px;
  line-height: 1.71;
  letter-spacing: 0.03em;
}

.jobs__title {
  font-weight: 700;
  font-size: 36px;
  line-height: 1.17;
  letter-spacing: 0.03em;
  color: var(--color-text1);
  text-align: center;
}

.jobs__text {
  display: flex;
  position: absolute;
  font-weight: bold;
  font-size: 14px;
  line-height: 16px;
  letter-spacing: 0.03em;
  color: var(--color-bg-body);
  width: 370px;
  height: 70px;
  align-items: center;
  justify-content: center;
  background-color: rgba(47, 48, 58, 0.8);
  bottom: 0;
  text-transform: uppercase;
}

.jobs__item {
  flex-basis: calc((100% - 3 * 30px) / 3);
  margin-left: 30px;
  text-align: center;
  margin-top: 30px;
  max-width: 370px;
  word-wrap: break-word;
  position: relative;
}

.team {
  background-color: var(--color-bg-btn);
  padding-top: 94px;
}

.section__title {
  font-weight: 700;
  font-size: 36px;
  line-height: 1.17;
  letter-spacing: 0.03em;
  color: var(--color-text1);
  text-align: center;
  margin-bottom: 50px;
}

.team__item {
  text-align: center;
  margin-left: 30px;
  margin-top: 30px;
  word-wrap: break-word;
  background: var(--color-bg-body);
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  border-radius: 0px 0px 4px 4px;
}

@media screen and (min-width: 480px) {
  .team__item {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    width: 450px;
    margin-left: 30px;
    margin-top: 30px;
  }
}

@media screen and (min-width: 768px) {
  .team__item {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    width: 354px;
    margin-left: 30px;
    margin-top: 30px;
  }
}

@media screen and (min-width: 1200px) {
  .team__item {
    flex-basis: calc((100% - 4 * 30px) / 4);
    max-width: 270px;
    margin-left: 30px;
    margin-top: 30px;
  }
}

.team__photo {
  margin: 0;
}

.team__box {
  display: block;
  max-width: 270px;
  padding-top: 30px;
  padding-bottom: 30px;
}

.team__subtitle {
  font-weight: 500;
  font-size: 16px;
  line-height: 1.18;
  letter-spacing: 0.03em;
  margin-bottom: 10px;
}

.team__text {
  color: var(--color-text2);
  font-size: 16px;
  line-height: 1.18;
  letter-spacing: 0.03em;
  word-wrap: break-word;
  margin-bottom: 16px;
}

.social-list {
  display: flex;
  justify-content: space-around;
  align-items: center;
  max-width: 206px;
  max-height: 44px;
  margin: 0 auto;
}

.social-list__item:not(:last-child) {
  margin-right: 10px;
}

.social-list__link {
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 50%;
  width: 44px;
  height: 44px;
  background-color: var(--color-bg-body);
  transition-property: background-color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.social-list__link:hover,
.social-list__link:focus {
  background-color: var(--color-link-focus);
  transition-property: background-color;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  border: none;
}

.social-list__svg {
  width: 20px;
  height: 20px;
  fill: #afb1b8;
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.social-list__link:hover .social-list__svg,
.social-list__link:focus .social-list__svg {
  background-color: transparent;
  fill: var(--color-bg-body);
  border: none;
  transition-property: fill;
  transition-duration: 250ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

.footer .social-list__svg {
  fill: var(--color-bg-body);
}

.clients {
  padding: 94px 0;
}

.clients__list {
  display: flex;
  justify-content: center;
  align-items: center;
}

.clients__item {
